self.addEventListener('install', (event) => {
  try {
    event.waitUntil(
      caches.open(PWA_CACHE_NAME)
        .then((cache) => {
          return cache.addAll(ALL_URLS);
        })
    );
  } catch (error) {
    console.error('[Service Worker] Error during install event: ', error);
  }
});

self.addEventListener('activate', (event) => {
  try {
    event.waitUntil(
      caches.keys().then((cacheNames) => {
        return Promise.all(
          cacheNames.map((cacheName) => {
            if (cacheName.startsWith(PWA_NAME) && cacheName !== PWA_CACHE_NAME) {
              return caches.delete(cacheName);
            }
          })
        );
      })
    );
  } catch (error) {
    console.error('[Service Worker] Error during activate event: ', error);
  }
});

self.addEventListener('fetch', (event) => {
  try {
    event.respondWith(
      caches.match(event.request)
        .then((response) => {
          if (response) {
            return response;
          }
          return fetch(event.request);
        })
    );
  } catch (error) {
    console.error('[Service Worker] Error during fetch event: ', error);
  }
});
