document.addEventListener("DOMContentLoaded", init, false);

function init() {
  if("serviceWorker" in navigator) {
    //console.log("[Bootstrap] Service Worker Supported");
    navigator.serviceWorker.register("/pwapp-serviceworker.min.js")
    .then((registration) => {
      //console.log("[Bootstrap] Service Worker Registration Successful");
    })
    .catch((err) => {
      //console.log("[Bootstrap] Service Worker Registration Failed", err);
    });
  } else {
    //console.log("[Bootstrap] Service Worker Not Supported");
  }
}
