document.addEventListener("DOMContentLoaded", init, false);
function init() {
  if ("serviceWorker" in navigator) {
    console.log("[Service Worker] Supported");
    navigator.serviceWorker.register("pwapp-serviceworker.min.js").then((registration) => {
      console.log("[Service Worker] Registration Successful", registration);
      button.onclick = () => {
      console.log("[Service Worker] Update Service Worker");
        registration.update();
      };
    }, (error) => {
      console.error("[Service Worker] Registration Unsuccessful: ${error}");
    });
  } else {
    console.error("[Service Worker] Not Supported");
  }
}
