/**
 * Announce ourselves.
 */
consoleWrapper('[Sainternet] Loaded');

/**
 * Initializes the web application by registering a service worker, if
 * supported.
 *
 * @function
 * @name initServiceWorker
 * @return {void}
 */
function initServiceWorker() {
  consoleWrapper(`[Sainternet] DOM content is fully loaded`);
  try {
    if ('serviceWorker' in navigator) {
      consoleWrapper(`[Sainternet] Service worker registration supported`);
      navigator.serviceWorker.register('/serviceworker.min.js', {scope: '/'})
      .then((registration) => {
        consoleWrapper(`[Sainternet] Service worker registration successful`);
      });
    } else {
      consoleWrapper(`[Sainternet] Service worker registration unsupported`);
    }
  } catch (error) {
    consoleWrapper(`[Sainternet] Service worker registration failed: ${error}`, `error`);
  }
}

/**
 * Executes the service worker init function on DOMContentLoaded
 */
document.addEventListener('DOMContentLoaded', initServiceWorker, false);
