self.addEventListener('install', function(event) {
  // "use strict";
  console.log('[Service Worker] Event: install');
  try {
    self.skipWaiting();
    event.waitUntil(
      caches.open(PWA_CACHE_NAME).then(function(cache) {
        console.log('[Service Worker] Installing cache: ' + PWA_CACHE_NAME);
        return cache.addAll(ALL_URLS);
      })
    );
  } catch (error) {
    console.error('[Service Worker] Error during install event');
  }
});

self.addEventListener('activate', function(event) {
  // "use strict";
  console.log('[Service Worker] Event: activate');
  try {
    if (self.clients && clients.claim) {
      console.log('[Service Worker] Claiming available clients');
      clients.claim();
    }
    event.waitUntil(
      caches.keys().then(function(keyList) {
        const cacheWhitelist = keyList.filter(function(key) {
          return key.indexOf(PWA_NAME);
        });
        cacheWhitelist.push(PWA_CACHE_NAME);
        return Promise.all(keyList.map(function(key, i) {
          if (cacheWhitelist.indexOf(key) === -1) {
            console.log('[Service Worker] Deleting cache: ' + keyList[i] );
            return caches.delete(keyList[i]);
          }
        }));
      })
    );
  } catch (error) {
    console.error('[Service Worker] Error during activate event');
  }
});

self.addEventListener('fetch', function(event) {
  // "use strict";
  console.log('[Service Worker] Event: fetch');
  try {
    console.log('[Service Worker] Fetch request: ' + event.request.url);
    event.respondWith(
      caches.match(event.request).then(function(request) {
        if (request) {
          console.log('[Service Worker] Responding from cache: ' + event.request.url);
          return request;
        } else {
          console.log('[Service Worker] Resource not cached, fetching: ' + event.request.url);
          return fetch(event.request);
        }
      })
    );
  } catch (error) {
    console.error('[Service Worker] Error during fetch event');
  }
});

self.addEventListener('message', (event) => {
  // "use strict";
  console.log('[Service Worker] Event: message');
  try {
    if (event.data.type === 'getDebugFlag') {
      const debugFlag = localStorage.getItem('DEBUG') ?? 'false';
      event.source.postMessage({type: 'debugFlag', value: debugFlag});
    } else if (event.data.type === 'setDebugFlag') {
      const debugFlag = event.data.value;
      localStorage.setItem('DEBUG', debugFlag);
    }
  } catch (error) {
    console.error('[Service Worker] Error during message event');
  }
});
