/**
 * look at me, i'm a jsdoc, ohhweeee
 */
function toggleDebugFlag() {
  // "use strict";
  const debugFlag = sessionStorage.getItem('DEBUG');
  let debugValue = false;
  if (debugFlag === 'true') {
    logDebugMessage('[Debug] Toggle DEBUG');
    debugValue = false;
    sessionStorage.setItem('DEBUG', 'false');
  } else {
    debugValue = true;
    sessionStorage.setItem('DEBUG', 'true');
    logDebugMessage('[Debug] Toggle DEBUG');
  }
  return debugValue;
}

/**
 * look at me, i'm a jsdoc, ohhweeee
 */
function logDebugMessage(message, method = 'log') {
  // "use strict";
  const debugFlag = sessionStorage.getItem('DEBUG');
  if (debugFlag === 'true') {
    console[method](message);
  }
}
