/**
 * Clears all key/value pairs in both localStorage and sessionStorage.
 *
 * @function
 * @name deleteLocalStorageAndSessionStorage
 * @return {void}
 */
function deleteLocalStorageAndSessionStorage() {
  // "use strict";
  let numLocalStorageDeleted = 0;
  for (let i = 0; i < localStorage.length; i++) {
    const key = localStorage.key(i);
    const value = localStorage.getItem(key);
    localStorage.removeItem(key);
    consoleWrapper(`[Debug] Deleted localStorage key: ${key}, value: ${value}`);
    numLocalStorageDeleted++;
  }
  let numSessionStorageDeleted = 0;
  for (let i = 0; i < sessionStorage.length; i++) {
    const key = sessionStorage.key(i);
    const value = sessionStorage.getItem(key);
    sessionStorage.removeItem(key);
    consoleWrapper(`[Debug] Deleted sessionStorage key: ${key}, value: ${value}`);
    numSessionStorageDeleted++;
  }
  if (numLocalStorageDeleted === 0 && numSessionStorageDeleted === 0) {
    consoleWrapper(`[Debug] No key/value pairs found to delete in localStorage or sessionStorage`);
  }
}

/**
 * Toggles the DEBUG flag in local storage.
 *
 * @function
 * @name toggleDebugFlag
 * @return {void}
 */
function toggleDebugFlag() {
  // "use strict";
  const debugFlag = localStorage.getItem('DEBUG');
  let debugValue = false;
  if (debugFlag === 'true') {
    consoleWrapper(`[Debug] DEBUG: ${debugValue}`);
    debugValue = false;
    localStorage.setItem('DEBUG', `${debugValue}`);
  } else {
    debugValue = true;
    localStorage.setItem('DEBUG', `${debugValue}`);
    consoleWrapper(`[Debug] DEBUG: ${debugValue}`);
  }
  if ('serviceWorker' in navigator) {
    navigator.serviceWorker.ready
      .then((registration) => {
        registration.active.postMessage({type: 'debug', value: debugValue});
      });
  }
  return debugValue;
}

/**
 * Logs a message to the console, with optional date and time information, if
 * the DEBUG flag is set to true.
 *
 * @function
 * @name consoleWrapper
 * @param {string} message - The message to be logged.
 * @param {string} [logType='log'] - The type of console method to use (log,
 * error, warn, etc.).
 * @param {boolean} [includeDateTime=false] - Whether to include the current
 * date and time in the log message.
 */
function consoleWrapper(message, logType = 'log', includeDateTime = false) {
  // "use strict";
  if (includeDateTime) {
    message = `${new Date().toLocaleString()} ${message}`;
  }
  if (typeof console === 'object' && typeof console[logType] === 'function') {
    const debugFlag = localStorage.getItem('DEBUG');
    if (debugFlag === 'true') {
      console[logType](message);
    }
  }
}

/**
 * Sends a "ping" message to the service worker and logs it.
 *
 * @name pingServiceWorker
 * @function
 * @return {void}
 */
function pingServiceWorker() {
  if ('serviceWorker' in navigator) {
    navigator.serviceWorker.ready
      .then((registration) => {
        registration.active.postMessage('ping');
        consoleWrapper('[Debug] Ping'), true;
      });
  }
}
