/**
 * look at me, i'm a jsdoc, ohhweeee
 */
function toggleDebugFlag() {
  // "use strict";
  logDebugMessage('[Debug] Toggle DEBUG');
  const debugFlag = localStorage.getItem('DEBUG') ?? 'false';
  const newDebugFlag = debugFlag === 'true' ? 'false' : 'true';
  localStorage.setItem('DEBUG', newDebugFlag);
  logDebugMessage('[Debug] Toggle DEBUG');
  const message = {type: 'setDebugFlag', value: newDebugFlag};
  navigator.serviceWorker.controller.postMessage(message);
}

/**
 * look at me, i'm a jsdoc, ohhweeee
 */
function logDebugMessage(message, level = 'log') {
  // "use strict";
  const debugFlag = localStorage.getItem('DEBUG') ?? 'false';
  if (debugFlag === 'true') {
    console[level](message);
  }
}
