/**
 * A very simple hammer.js gesture event handler
 *
 * @function
 * @name detectGestureEvents
 */
function detectGestureEvents() {
  const gestureObject = new Hammer(document);
  gestureObject.on('swipeleft', function() {
    consoleWrapper('[Gesture Handler] Detected gesture: swipeleft');
    if (document.querySelector('.mdl-layout__drawer').classList.contains('is-visible')) {
      mdlLayout.MaterialLayout.toggleDrawer();
    }
  });
  gestureObject.on('swiperight', function() {
    consoleWrapper('[Gesture Handler] Detected gesture: swiperight');
    if (!document.querySelector('.mdl-layout__drawer').classList.contains('is-visible')) {
      document.querySelector('.mdl-layout__drawer-button').click();
    }
  });
  gestureObject.on('tap press', function(event) {
    consoleWrapper(`[Gesture Handler] Detected gesture: ${event.type}`);
});
}

/**
 * Execute the aforementioned.
 */
detectGestureEvents();
