function insertHeader() {
  var meta = document.createElement("meta");

  //console.log("[Insert Header] Inserting Cache-Control Header");
  meta = document.createElement("meta");
  meta.setAttribute("http-equiv", "Cache-Control");
  meta.setAttribute("content", "max-age=1209600");
  document.head.appendChild(meta);

  //console.log("[Insert Header] Inserting Content-Security-Policy Header");
  meta = document.createElement("meta");
  meta.setAttribute("http-equiv", "Content-Security-Policy");
  meta.setAttribute("content", "default-src 'self';font-src 'self' fonts.gstatic.com googleapis.com;script-src 'self' hammerjs.github.io;style-src 'self' fonts.googleapis.com");
  document.head.appendChild(meta);

  //console.log("[Insert Header] Inserting Content-Security-Policy Header");
  meta = document.createElement("meta");
  meta.setAttribute("http-equiv", "Content-Security-Policy");
  meta.setAttribute("content", "upgrade-insecure-requests");
  document.head.appendChild(meta);

  //console.log("[Insert Header] Inserting Content-Type Header");
  meta = document.createElement("meta");
  meta.setAttribute("http-equiv", "Content-Type");
  meta.setAttribute("content", "text/html; charset=utf-8");
  document.head.appendChild(meta);

  //console.log("[Insert Header] Inserting X-UA-Compatible Header");
  meta = document.createElement("meta");
  meta.setAttribute("http-equiv", "X-UA-Compatible");
  meta.setAttribute("content", "IE=edge");
  document.head.appendChild(meta);

  //console.log("[Insert Header] Inserting X-XSS-Protection Header");
  meta = document.createElement("meta");
  meta.setAttribute("http-equiv", "X-XSS-Protection");
  meta.setAttribute("content", "1");
  document.head.appendChild(meta);
}

insertHeader();
