function insertHeaders() {
  try {
    let meta = document.createElement('meta');

    window.console && console.log("[Insert Header] Inserting Cache-Control header");
    meta = document.createElement('meta');
    meta.setAttribute('http-equiv', 'Cache-Control');
    meta.setAttribute('content', 'max-age=1209600');
    document.head.appendChild(meta);

    window.console && console.log("[Insert Header] Inserting Content-Security-Policy header");
    meta = document.createElement('meta');
    meta.setAttribute('http-equiv', 'Content-Security-Policy');
    meta.setAttribute('content', 'upgrade-insecure-requests');
    document.head.appendChild(meta);

    window.console && console.log("[Insert Header] Inserting Content-Type header");
    meta = document.createElement('meta');
    meta.setAttribute('http-equiv', 'Content-Type');
    meta.setAttribute('content', 'text/html; charset=utf-8');
    document.head.appendChild(meta);

    window.console && console.log("[Insert Header] Inserting X-UA-Compatible header");
    meta = document.createElement('meta');
    meta.setAttribute('http-equiv', 'X-UA-Compatible');
    meta.setAttribute('content', 'IE=edge');
    document.head.appendChild(meta);

    window.console && console.log("[Insert Header] Inserting X-XSS-Protection header");
    meta = document.createElement('meta');
    meta.setAttribute('http-equiv', 'X-XSS-Protection');
    meta.setAttribute('content', '1');
    document.head.appendChild(meta);
  } catch (error) {
    window.console && console.error('[Insert Header] Error inserting headers: ', error);
  }
}

insertHeaders();
