/**
 * Announce ourselves.
 */
consoleWrapper(`[Load Mono Fonts] Loaded`);

/**
 * Loads Roboto Mono fonts if not already loaded and adds a 'mono-fonts-loaded'
 * class to the document.
 *
 * @function
 * @name loadMonoFonts
 * @returns {void}
 */
function loadMonoFonts() {
  if (sessionStorage.monoFontsLoaded) {
    consoleWrapper('[Load Fonts] Mono fonts already loaded');
    document.documentElement.classList.add('mono-fonts-loaded');
    return;
  }
  if ('fonts' in document) {
    Promise.all([
      document.fonts.load('1em Roboto Mono'),
      document.fonts.load('700 1em Roboto Mono'),
      document.fonts.load('italic 1em Roboto Mono'),
      document.fonts.load('italic 700 1em Roboto Mono'),
    ]).then((_) => {
      consoleWrapper('[Load Fonts] Mono fonts loaded');
      document.documentElement.classList.add('mono-fonts-loaded');
      sessionStorage.monoFontsLoaded = true;
    });
  }
}

/**
 * Execute the aforementioned.
 */
loadMonoFonts();
