/**
 * Announce ourselves.
 */
consoleWrapper(`[Motion Sickness] Loaded`);

/**
 * Enables motion styles by removing the "motion-sickness" class from the root element
 * if the user's preference for reduced motion is not specified.
 *
 * @function
 * @name enableMotionStyles
 * @return {void}
 */
function enableMotionStyles() {
  if (window.matchMedia('(prefers-reduced-motion: no-preference)').matches) {
    document.documentElement.classList.remove('motion-sickness');
  }
}

/**
 * Execute the aforementioned.
 */
enableMotionStyles();
