/**
 * Retrieves a random URL from the creatively named 'randomURLs' array and
 * inserts it into a specific HTML element with the similarly inventive
 * ID 'sainternet-urls'.
 *
 * @function
 * @name getURLs
 * @return {void}
 */
function getURLs() {
  const randomURL = Math.floor(Math.random()*randomURLs.length);
  consoleWrapper('[Random URL] Inserting random sainternet.xyz URL');
  document.getElementById('sainternet-urls').innerHTML=randomURLs[randomURL];
}

/**
 * Event listener function that gets a random phrase and inserts it into a
 * specific HTML element when the page loads.
 *
 * @function
 * @name onload
 * @returns{void}
 */
onload = function() {
  consoleWrapper('[Random URL] Get random sainternet.xyz URL');
  getURLs();
};
