const mdlLayout = document.querySelector('.mdl-layout');
const mdlDrawer = document.querySelector('.mdl-layout__drawer');

if (mdlDrawer) {
  logDebugMessage('[Toggle Drawer] Add event listener');
  mdlDrawer.addEventListener('click', closeDrawer);
}

/**
 * look at me, i'm a jsdoc, ohhweeee
 */
function closeDrawer() {
  // "use strict";
  logDebugMessage('[Toggle Drawer] Event: click');
  mdlLayout.MaterialLayout.toggleDrawer();
}
