/**
 * Announce ourselves.
 */
consoleWrapper('[Toggle Drawer] Loaded');

/**
 * Some helper variables used later are defined here.
 */
const mdlLayout = document.querySelector('.mdl-layout');
const mdlDrawer = document.querySelector('.mdl-layout__drawer');

/**
 * Adds an event listener to the Material Design Lite drawer element and
 * executes the closeDrawer function on click.
 *
 * @function
 * @name addDrawerEventListener
 * @param {HTMLElement} mdlDrawer - The Material Design Lite drawer element.
 * @return {void}
 */
if (mdlDrawer) {
  consoleWrapper('[Toggle Drawer] Adding event listener');
  mdlDrawer.addEventListener('click', closeDrawer);
}

/**
 * Closes the Material Design Lite drawer element and logs the event to the
 * console.
 *
 * @function
 * @name closeDrawer
 * @returns {void}
 */
function closeDrawer() {
  consoleWrapper('[Toggle Drawer] Event: click');
  mdlLayout.MaterialLayout.toggleDrawer();
}
