function deleteLocalStorageAndSessionStorage(){let e=0;for(let t=0;t<localStorage.length;t++){let g=localStorage.key(t),l=localStorage.getItem(g);localStorage.removeItem(g),console.log(`[Debug] Deleted localStorage key: ${g}, value: ${l}`),e++}let o=0;for(let u=0;u<sessionStorage.length;u++){let a=sessionStorage.key(u),s=sessionStorage.getItem(a);sessionStorage.removeItem(a),console.log(`[Debug] Deleted sessionStorage key: ${a}, value: ${s}`),o++}0===e&&0===o&&console.log("[Debug] No key/value pairs found to delete in localStorage or sessionStorage")}function toggleDebugFlag(){let e=localStorage.getItem("DEBUG"),t=!1;"true"===e?(logDebugMessage("[Debug] Toggle DEBUG: false"),t=!1,localStorage.setItem("DEBUG","false")):(t=!0,localStorage.setItem("DEBUG","true"),logDebugMessage("[Debug] Toggle DEBUG: true"))}function logDebugMessage(e,t="log"){let g=localStorage.getItem("DEBUG");"true"===g&&console[t](e)}
